INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('127', '도요새를 뜻하는 영어 단어에서 유래했습니다. 18세기 후반 영국군 장교들 사이에서 몸집이 작고 움직임이 빠른 도요새를 사냥할 때 명중률이 높은 사람을 가리켜 이것이라 부르기 시작했는데요. 이것은? ', '', 'ㅅㄴㅇㅍ', '','COMMON','TEXT');
