INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('128', '이것은 로마 시대 전쟁에서 병사들의 상처를 치료하는 만병통치약으로 불렀다고 합니다. 이것 한 줌의 값은 일반 병사들의 월급과는 비교할 수 없을 정도로 비싸다는데요. 또한 이것을 신성하게 여긴 네로 황제는 아내가 죽었을 때 장례식에 이것을 태웠다고 합니다. 후추, 정향과 함께 세계 3대 향신료라 불리는 이것은 무엇일까요?', '', 'ㄱㅍ', '','COMMON','TEXT');
