INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('129', '옛 선조들은 정월 대보름에 이 동물로 한 해 농사를 점쳤다고 합니다. 이 동물에게 오곡밥과 나물을 준 뒤 오곡밥을 먼저 먹으면 풍년을, 나물을 먼저 먹으면 흉년이 될 것이라 예측했다고 하는데요. 예로부터 풍요로움을 상징하는 이 동물은 무엇일까요?', '', 'ㅅ', '','COMMON','TEXT');
