INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('130', '손과 팔을 연결해주는 손목의 잘록한 부분을 이것이라 합니다. 이것은 악기를 연주하거나 그림을 그리는 등 손을 자유자재로 움직일 수 있게 도와주는 역할을 하는데요. 이러한 이유로 일재간이 뛰어난 사람에게 흔히 이것이 좋다고 표현합니다. 이것은 무엇일까요?', '', 'ㅅㅇ', '','COMMON','TEXT');
