INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('131', '이것은 늘 옆자리에 두고 가르침으로 삼는 글이라는 뜻입니다. 고대 중국학자인 최원은 스스로 지켜야 할 문장들을 칼에 새겨 자신이 앉은 오른쪽에 놓고 평생 되새기며 살았다고 전해지는데요. 이후 사람들이 그 문장을 가리켜 이것이라 불렀다고 합니다. 자리 오른쪽에 새겨두는 글이라는 뜻의 이것은 무엇일까요?', '', 'ㅈㅇㅁ', '','COMMON','TEXT');
