INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('132', '이곳은 물이 흐르는 도랑처럼 생겼다 하여 수구라고도 불리는 신체 부위입니다. 예로부터 우리 조상들은 이곳의 길이와 깊이를 보고 수명의 길고 짧음을 판단하기도 했다는데요. 명치, 관자놀이와 함께 우리 몸의 대표 급소 부위로 꼽히는 이곳은 어디일까요?', '', 'ㅇㅈ', '','COMMON','TEXT');
