INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('133', '이것은 그리스 로마 신화에 나오는 뒤늦게 알다라는 뜻을 가진 신의 이름에서 유래된 것입니다. 판도라와 결혼한 그는 판도라가 열어선 안 될 상자를 열어 인류가 불행에 휩싸이게 되자 뒤늦게 후회를 했다는데요. 오늘날 소설이나 연극이 끝난 후 덧붙여지는 이야기로 쓰이는 이것은 무엇일까요?', '', 'ㅇㅍㄹㄱ', '','COMMON','TEXT');
