INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('134', '이것은 먼 데서 일어난 일까지 꿰뚫어 보는 능력을 의미합니다. 중국 위나라의 관직자 양일이 부하를 시켜 끊임없이 정보를 모아 먼 곳의 일까지 잘 파악하자 사람들은 양일이 이것을 가졌다고 했다는데요. 또한 이것은 국내 최초로 개발된 정지 궤도 위성의 이름이기도 한데요. 기상 및 해양을 관측하여 우주의 눈이라 불리는 이것은 무엇일까요?', '', 'ㅊㄹㅇ', '','COMMON','TEXT');
