INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('135', '이것은 그리스어로 인간을 닮은 것이라는 뜻을 가진 로봇입니다. 1886년 프랑스 SF 소설미래의 이브에 처음 등장한 이것은 인간과 똑같은 외모를 가지고 동작과 표정까지 비슷하게 따라한 것이 특징이었는데요. 겉보기에 인간과 구별할 수 없어 인조인간이라고도 부르는 이 로봇은 무엇일까요?', '', 'ㅇㄷㄹㅇㄷ', '','COMMON','TEXT');
