INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('136', '이것은 당나라 시인 가도가 시에 들어갈 한자를 고민하는 과정에서 유래된 말입니다. 가도는 시의 마지막 구절을 문을 밀다와 문을 두드린다 두 가지를 놓고 한참 고민했다고 하는데요. 여기서 유래되어 오늘날 글을 쓸 때 여러 번 생각하여 고치고 다듬는 작업을 가리켜 이것이라 합니다. 이것은 무엇일까요?', '', 'ㅌㄱ', '','COMMON','TEXT');
