INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('137', '이것은 모든 사람을 위한 것이라는 라틴어에서 유래했는데요. 과거 프랑스에서는 다양한 계급의 사람들이 함께 타는 마차를 이것이라고 불렀다고 합니다. 여기서 유래해 오늘날 하나의 주제 안에 여러 명의 감독이 연출한 단편을 합쳐 놓은 영화를 가리켜 이것 영화라고 하는데요. 이것은 무엇일까요?', '', 'ㅇㄴㅂㅅ', '','COMMON','TEXT');
