INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('138', '이것은 아무 모양이나 만들 수 있다는 뜻의 그리스어에서 유래됐습니다. 이것은 열이나 압력을 가해, 원하는 모양으로 쉽게 만들 수 있어 20세기 인류 최고의 발명품으로 불렸는데요. 그러나 오늘날 이것은 심각한 환경 오염을 일으키고 있어 전 세계적으로 사용량을 줄이기 위한 규제가 시행되고 있습니다. 이것은 무엇일까요?', '', 'ㅍㄹㅅㅌ', '','COMMON','TEXT');
