INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('139', '이것은 파도의 높이가 만 장에 이른다는 고사성어인데요. 약 3m를 나타내는 길이 단위인 장이 만 개나 된다는 뜻으로 일이 진행되는 과정이나 기복이 심한 삶을 비유하는 말로 쓰이고 있습니다. 흔히 우여곡절이 많은 삶을 가리켜 이것한 삶이라고 표현하는데요. 이것은 무엇일까요?', '', 'ㅍㄹㅁㅈ', '','COMMON','TEXT');
