INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('140', ' 이것은 원래 터를 잡은 자취를 뜻하는 말입니다. 집을 세웠던 터를 보면 주춧돌을 놓았던 자리나 기둥을 세운 흔적이 남게 되는데요. 터를 세운 흔적이 없어진 믿을 수 없는 상황에 빗대어 오늘날 이것이 없다라는 말은 어떤 말이나 행동이 전혀 근거가 없을 때를 비유하는 말로 쓰이고 있습니다. 이것은 무엇일까요?', '', 'ㅌㅁㄴ', '','COMMON','TEXT');
