INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('141', '해바라기처럼 노란색 꽃을 피우는 식물인 이것의 뿌리는 생김새가 감자와 비슷해 돼지감자라고도 불리는데요. 이것은 따로 심지 않아도 논밭을 가리지 않고 아무데서나 자라서 사람들을 당황스럽게 했다고 합니다. 오늘날에는 행동이나 사고방식이 엉뚱한 사람을 가리켜 이것이라 부르기도 하는데요. 이것은 무엇일까요?', '', 'ㄸㄸㅈ', '','COMMON','TEXT');
