INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('142', '이것은 원래 돗자리를 만다는 뜻입니다. 돗자리를 말 듯 빠른 기세로 영토를 확장하거나 세력을 넓히는 상황에서 쓰는데요. 여기에 빗대어 가수들의 앨범이 여러 음원 사이트에서 상위권을 차지할 때 각종 차트를 이것했다라고 표현하기도 합니다. 이것은 무엇일까요?', '', 'ㅅㄱ', '','COMMON','TEXT');
