INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('143', '서로 이기려고 힘을 겨뤄 다투는 모습을 이것이라고 하는데요 이것은 짐승들이 영역이나 먹이를 차지하기 위해 뿔을 부딪치거나 쫓으며 싸우는 모습에서 비롯된 말이라고 합니다. 우승을 가리켜 두 팀이 경쟁할때 이것전이 벌어졌다고 하는데요. 이것은 무엇일까요?', '', 'ㄱㅊ', '','COMMON','TEXT');
