INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('146', ' 이것은 프랑스어로 금융이라는 뜻을 가진 디저트입니다. 19세기 프랑스 증권거래소 근처 한 빵집에서 바쁜 증권인들을 위해 손에 묻지 않고 먹기 쉬운 사이즈의 이것을 개발했다고 하는데요. 금괴처럼 생겨 프랑스에서는 재물 운을 가져다주는 디저트로 불리는 이것은 무엇일까요?', '', 'ㅎㄴㅅㅇ, ㅍㄴㅅㅇ', '','COMMON','TEXT');
