INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('148', ' 이것은 원래 강물을 함께 사용하는 사람들이라는 라틴어에서 유래됐는데요. 강물이 풍족할 때는 함께 나눠 쓰는 이웃이지만 부족할 때는 싸움을 벌이는 모습에 빗대어 생겨난 말입니다. 오늘날 같은 분야에서 서로 이기거나 앞서려고 겨루는 사람을 뜻하는 이것은 무엇일까요?', '', 'ㄹㅇㅂ', '','COMMON','TEXT');
