INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('149', '이것은 야외에서 부처님의 말씀을 듣는 자리라는 뜻의 불교용어입니다. 석가모니가 영취산에서 화경을 설법할 때 삼백만 명이 모여 법당 밖까지 자리를 깔 정도로 어수선한 모습에서 비롯된 말인데요. 오늘날 질서가 없고 시끌벅적한 모습을 비유하는 말로 쓰이는 이것은 무엇일까요?', '', 'ㅇㄷㅂㅅ', '','COMMON','TEXT');
