INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('150', '이것은 깨끗이 잊지 못하고 남아있는 마음을 뜻하는 말입니다. 과거 부모님이 돌아가신 뒤 1년 후에 입는 상복을 연복이라 했는데요. 부모님이 돌아가신 것을 믿고 싶지 않아 아직 연복을 입을 때가 아니다.라고 생각하는 마음에서 이것이 유래되었다고 합니다. 이것은 무엇일까요?', '', 'ㅁㄹ', '','COMMON','TEXT');
