INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('151', '이것은 그리스어로 그림이라는 뜻에서 유래된 말입니다. 예로부터 기독교에서는 신성시하는 인물의 초상화를 이것이라 부르며 존경의 대상으로 여겼다고 하는데요. 여기서 유래하여 오늘날 어떤 분야를 상징하거나 그 분야에서 최고인 사람을 이것이라고 합니다. 이것은 무엇일까요?', '', 'ㅇㅇㅋ', '','COMMON','TEXT');
