INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('152', '제우스는 신들이 창조한 인류 최초의 여성인 판도라에게 절대 열어 보지 말라는 당부와 함께 상자를 선물합니다. 호기심을 못 이긴 판도라가 상자를 열자 인간의 모든 불행과 질병, 고통 등이 세상에 쏟아져 나온 뒤, 이것만 유일하게 남았다고 하는데요. 앞으로 잘 될 수 있는 가능성을 뜻하는 이것은 무엇일까요?', '', 'ㅎㅁ', '','COMMON','TEXT');
