INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('153', '이것은 원래 곡식에 섞인 잡티를 골라내기 위해 키질을 하던 동작을 가리키던 말입니다. 키질은 키에 곡식을 올린 뒤 위아래로 정신없이 흔들어야 하는데요. 이러한 동작에 비유해, 오늘날 돌아다니며 장난치는 아이나 철없이 경망하게 행동하는 사람을 이것이라 부르게 되었다고 합니다. 이것은 무엇일까요?', '', 'ㄲㅂㅇ', '','COMMON','TEXT');
