INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('154', '재활용을 상징하는 마크는 이것 모양을 본 따 만들었다고 합니다. 이것은 19세기 독일의 수학자가 발견해 그의 이름을 따 지었는데요. 기다란 직사각형 종이를 한 번 비틀어 양쪽 끝을 맞붙인 것으로 안과 밖의 구별이 없는 게 특징인 수학 도형을 가리키는 이것은 무엇일까요?', '', 'ㅁㅂㅇㅅㅇ ㄸ', '','COMMON','TEXT');
