INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('155', '이것은 과거 부엌이나 창고에 나무문이 비틀리는 것을 막기 위해 덧붙인 나무를 일컫는 말입니다. 나무문에 대각선으로 삐딱하게 놓은 것에서 유래된 말로 어떤 일에 반항하는 말이나 행동을 할 때 이것을 놓는다는 표현을 쓰는데요. 무엇일까요?', '', 'ㅇㄱㅈ', '','COMMON','TEXT');
