INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('157', '이것은 우리나라 전통 가옥에서 채광이나 통풍을 위해 뚫어놓은 창을 뜻하는 말입니다. 새벽에 이것을 두들기는 소리를 듣고 놀라 잠에서 깨는 상황에 빗대어, 뜻밖의 말이나 행동을 비유할 때 쓰이는 이것은 무엇일까요?', '', 'ㅂㅊ', '','COMMON','TEXT');
