INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('158', '이것은 둘러싸인 정원을 뜻하는 고대 페르시아어에서 유래한 말입니다. 당시 페르시아 제국의 정원은 맑은 시내가 흐르고 희귀한 나무와 꽃이 만발한 모습이었다는데요. 여기서 유래해, 오늘날 걱정이나 근심 없이 행복을 누릴 수 있는 곳을 뜻하는 이것은 무엇일까요?', '', 'ㅍㄹㄷㅇㅅ', '','COMMON','TEXT');
