INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('159', '이것은 원래 소리가 나는 화살이라는 뜻인데요. 과거 중국에서는 이것을 허공에 쏴 전쟁의 시작을 알렸다고 합니다. 오늘날 이것은 사물의 발단이나 어떤 일의 시작을 비유하는 말로 쓰이고 있는데요. 이것은 무엇일까요?', '', 'ㅎㅅ', '','COMMON','TEXT');
