INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('160', '고대 이집트에서는 신체 부위인 이것의 색상으로 신분을 구분했다고 합니다. 왕족들은 이것에 진한 적색을 칠했으며, 신분이 낮을수록 색이 점점 옅어졌다고 하는데요. 예로부터 이것만 보고 건강상태를 알 수 있다 하여 우리 몸의 작은 거울로 비유되기도 하는 이것은 무엇일까요?', '', 'ㅅㅌ', '','COMMON','TEXT');
