INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('161', '흔히 다재다능하거나 다양한 매력으로 이미지 변신하는 사람을 이것에 비유합니다. 이것은 숲의 요정이라 불리는 새로, 무지개와 같이 여러 가지 색의 깃털을 가진 것이 특징인데요. 천연기념물 204호로 지정된 이 새는 무엇일까요?', '', 'ㅍㅅㅈ', '','COMMON','TEXT');
