INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('162', '19세기 유럽에서 콜레가 유행하자 향신료인 이것을 소독약으로 사용했다고 합니다. 당시에는 악취가 모든 병의 근원이라 여겨져 향이 강한 이것으로 소독한 것인데요. 과거 실크로드와 중국을 통해 전해져서 호국의 산초라 불렸던 이 향신료는 무엇일까요?', '', 'ㅎㅊ', '','COMMON','TEXT');
