INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('163', '매년 영국의 버킹엄 궁전에서는 종교 분야에서 인류를 위해 큰 업적을 남긴 사람에게 수여하는 이 상의 시상식이 열립니다. 이 상은 노벨상에 종교 부문이 없는 것을 안타깝게 생각한 영국의 사업가가 만든 상으로 1973년 1회 수상자는 가난한 자들의 어머니라 불리는 마더 테레사 수녀였는데요. 종교계의 노벨상이라 불리는 이 상은 무엇일까요?', '', 'ㅌㅍㅌㅅ', '','COMMON','TEXT');
