INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('164', '15세기 창제된 훈민정음에는 이것으로 음절의 높낮이를 구분했는데요. 이것은 글자 왼쪽에 찍어 놓은 둥근 점을 가리키는 말로 이 점의 개수를 통해 음절의 높낮이를 구분했다고 합니다. 흔히 어떤 분야에서 흔적을 남길 만한 새롭거나 뛰어난 일을 할 때 이것을 찍었다라는 표현을 쓰기도 하는데요. 이것은 무엇일까요?', '', 'ㅂㅈ', '','COMMON','TEXT');
