INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('165', '이것은 고대 그리스의 서사시 오디세이아에 등장하는 인물에서 유래가 됐습니다. 이 인물은 트로이 전쟁에 나가는 친구 오디세우스를 대신해 십 년간 친구 아들의 스승이자 상담가 역할을 해주었습니다. 이 인물의 이름에서 유래해, 오늘날 경험이 부족한 이들에게 지도나 조언을 해 주는 사람을 이것이라고 부릅니다. 이것은 무엇일까요?', '', 'ㅁㅌ', '','COMMON','TEXT');
