INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('166', '중국 한나라 철학자 왕충의 저서인 논형에는 이 동물이 옳고 그름을 가릴 수 있는 신성한 동물로 기록돼 있는데요. 그는 선악을 구분하는 상상 속 동물인 해태를 뿔이 하나 달린 이 동물이라고 표현했습니다. 한자 옳을 의에는 이 동물의 한자가 포함돼 있는데요. 이 동물은 무엇일까요?', '', 'ㅇ', '','COMMON','TEXT');
