INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('167', '이것은 원래 술잔에 따르는 술의 양을 헤아리는 것을 말합니다. 술을 따를 때는 상대방 주량에 따라 알맞게 따라야 하는데요. 여기서 유래해 오늘날 이것은 이리저리 비추어 본 뒤 알맞게 고려한다는 뜻으로 법원은 이것 사유에 따라 형을 선고하기도 합니다. 이것은 무엇일까요?', '', 'ㅊㅈ', '','COMMON','TEXT');
