INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('170', '책을 읽던 곳을 표시하기 위해 끼워둔 줄을 이것줄이라고 합니다. 본래 이것은 흔적 또는 표적의 의미로 쓰였지만 오늘날에는 좋은 결과를 얻은 후의 느낌을 표현할 때 사용하는데요. 일을 한 뒤에 얻은 만족스러운 마음을 뜻하는 이것은 무엇일까요?', '', 'ㅂㄹ', '','COMMON','TEXT');
