INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('171', '이것은 일정하지 않고 시세에 따라 달라지는 값을 부르는 말입니다. 과거에는 곡식의 가격이 일정하지 않아 날마다 다른 값을 매겼다는데요. 일정한 값이 정해지지 않았을 때 이것 없다라고 말한대서 유래해 갑작스럽거나 엉뚱한 행동을 비유할 때 쓰이는 이것은 무엇일까요?', '', 'ㄸㄱ', '','COMMON','TEXT');
