INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('172', '이것은 그리스어로 두 개의 제안이라는 뜻인데요. 게임 이론 중 두 사람이 협력하면 가장 좋은 결과를 얻을 수 있지만 자신의 이익만을 선택할 경우 둘 다 손해를 보개 되는 상황을 죄수의 이것이라고 합니다. 이것은 무엇일까요?', '', 'ㄷㄹㅁ', '','COMMON','TEXT');
