INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('175', '이것은 앞일에 대해 쓸데없는 걱정을 하는 걸 뜻하는 말입니다. 옛날 중국 기나라에 하늘이 무너지고 땅이 꺼질까 무서워 집 밖에도 못 나가고 매일 걱정만 하는 사람이 있었다고 하는데요. 기나라 사람이 안 해도 될 걱정을 하는 데서 유래해 일어나지 않을 일을 지나치게 염려하는 것을 이것이라고 합니다. 이것은 무엇일까요?', '', 'ㄱㅇ', '','COMMON','TEXT');
