INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('176', '이것은 원래 귀금속의 순도와 진품 여부를 확인하는 돌판을 가리키는데요. 과거에는 이것에 금을 문질렀을 때 나오는 빛깔로 순도를 시험했다고 합니다. 이후 의미가 확대돼, 오늘날 이것은 어떤 사물의 가치나 사람의 능력을 평가하는데 기준이 될 만한 것을 비유하는 말로 쓰이고 있는데요. 문학을 평가할 때 기준이 되는 위대한 문인들의 명문장을 뜻하기도 하는 이것은 무엇일까요?', '', 'ㅅㄱㅅ', '','COMMON','TEXT');
INSERT INTO `quizdot`.`answer` (`quiz_id`, `answer`) VALUES ('176', '시금석');
INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('177', '고려 말 이방원은 조선 건국을 위해 정몽주를 설득하고자 하여가라는 시조를 지었는데요. 조선 건국을 반대하는 뜻의 단심가로 답한 정몽주는 이곳에서 이방원의 부하들에게 죽음을 맞았다고 합니다. 정몽주가 죽은 자리에 충절의 상징인 대나무가 솟아 이름 붙여진 이곳은 어디일까요?', '', 'ㅅㅈㄱ', '','COMMON','TEXT');
