INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('178', '페루의 안데스 산맥에 위치한 이곳은 늙은 봉우리라는 뜻의 고대 도시입니다. 이곳은 해발 2,400m에 세워진 잉카 문명의 유적지로 1911년 한 고고학자에 의해 발견되기 전까진 존재가 알려지지 않아 잃어버린 공중도시라고 불렸는데요. 세계 7대 불가사의 중 하나로 꼽히는 이곳은 어디일까요?', '', 'ㅁㅊㅍㅊ', '','COMMON','TEXT');
