INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('179', '이것은 식물이라는 그리스어와 죽이다를 뜻하는 라틴어의 합성어입니다. 1942년 러시아의 생물학 교수가 처음 사용해 알려진 말로, 숲속 식물들이 만들어 내는 살균성 물질을 이것이라 하는데요. 한 조사에 의하면, 지리산의 이것이 도심 속 공원보다 10배 이상이나 많다고 합니다. 이것은 무엇일까요?', '', 'ㅍㅌㅊㄷ', '','COMMON','TEXT');
