INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('180', '이것은 로마 신화에 등장하는 문을 지키는 신입니다. 이것은 서로 반대편을 보고 있는 두 얼굴을 가지고 있어, 문을 경계로 펼쳐지는 안과 밖의 세계를 모두 관리했다고 하는데요. 두 얼굴을 가진 모습을 빗대어 오늘날 선과 악을 모두 지닌 이중적인 사람을 이것에 비유합니다. 이것은 무엇일까요?', '', 'ㅇㄴㅅ', '','COMMON','TEXT');
