INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('181', '전혀 다른 분야가 만나 창조적인 아이디어를 이끌어 내는 것을 이것 효과라 하는데요. 이는 15세기 이탈리아에서 예술가나 학자들이 교류할 수 있게 후원하여, 르네상스 시대를 이끈 이 가문의 이름에서 유래된 것입니다. 현재 악부로 남아있는 가장 오래된 오페라인 <에우리디체>를 이 가문의 결혼식에서 처음 선보였다고 하는데요. 이 가문의 이름은 무엇일까요?', '', 'ㅁㄷㅊ', '','COMMON','TEXT');
