INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('182', '이것은 집의 기둥 위에서 무거운 지붕을 받쳐주는 나무를 가리키는 말입니다. 이것은 집의 뼈대를 이루는 가장 중요한 재료이기 때문에 흔히 집안의 운명을 쥐고 있다고 할 만큼 중요한 사람을 이것에 비유하기도 하는데요. 이것은 무엇일까요?', '', 'ㄷㄷㅂ', '','COMMON','TEXT');
