INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('183', '시리즈의 연속성을 버리고 새로운 시리즈를 만드는 영화를 이것이라 합니다. 원래 컴퓨터 용어인 이것은 영화에서는 속편이 반복되면서 스토리가 정체되는 것을 탈피하기 위한 방법으로 쓰이는데요. 어떤 시리즈 작품에서 골격이나 등장인물만 차용하고, 캐릭터와 이야기는 새롭게 해석하는 것을 뜻하는 이것은 무엇일까요?', '', 'ㄹㅂㅌ', '','COMMON','TEXT');
