INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('184', '이것은 원래 이탈리아로 적을 향해 무기를 들고 돌진하라라는 뜻의 군사 용어였는데요. 과거에는 전쟁 중 적의 심상치 않은 움직임이 포착될 때 지휘관이 이것을 외쳤다고 합니다. 오늘날 미리 설정한 조건에 맞춰 음을 내는 장치를 뜻하는 이것은 무엇일까요?', '', 'ㅇㄹ', '','COMMON','TEXT');
