INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('185', '이곳은 스페인어로 금가루를 칠한 사람이라는 뜻의 상상 속 도시입니다. 16세기 아마존강 인근에 위치한 이곳에는 온몸에 황금을 칠한 왕이 살고 있다는 소문이 퍼졌는데요. 이에 스페인 탐험가들이 일확천금을 노리고 떠났지만 이곳을 발견한 자는 아무도 없었다고 합니다. 오늘날 전설 속 황금 도시라 전해져오는 이곳은 어디일까요?', '', 'ㅇㄷㄹㄷ', '','COMMON','TEXT');
